import React, {useState} from 'react';

import { Link } from "react-router-dom";
import MenuIcon from '@material-ui/icons/Menu';
import PeopleIcon from '@material-ui/icons/People';
import ScoreIcon from '@material-ui/icons/Score';
import SearchIcon from '@material-ui/icons/Search';
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';
import {Drawer, List, ListItem, ListItemText} from '@material-ui/core/';
import {makeStyles} from '@material-ui/core/styles';

const navBars = [{text: "My Team", link: "/fantasy-team", icon: <PeopleIcon />}, {text: "Search Player", link: "/search-players", icon: <SearchIcon />}, {text: "Live Score", link: "/fantasy-team", icon: <ScoreIcon />}];

const useStyles = makeStyles({
    hamburger: {
        position: "fixed",
        top: 20,
        right: 20,
        backgroundColor: "#3f51b5",
        color: "white",
        borderRadius:5,
        cursor: "pointer",
        zIndex: 100
    },
    item: {
        cursor: "pointer",
        color: "white",
        backgroundColor: "#3f51b5",
        marginTop: 5,
        marginBottom: 5,
    }
})

const SideNav = () => {
    const classes = useStyles();
    const [state, setState] = useState(false);
    return (
        <div className="sidenav">
            <MenuIcon className={classes.hamburger} onClick={() => setState(!state)} />
            <Drawer anchor="right" open={state} onClose={() => setState(false)}>
                <List>
                    <ArrowForwardIcon className={classes.item} onClick={() => setState(false)} />
                    {navBars.map(nav => {
                        return <Link onClick={() => setState(false)} style={{textDecoration: "none"}} to={nav.link}>
                            <ListItem className={classes.item}>
                                <ListItemText primary={nav.text} />
                                {nav.icon}
                            </ListItem>
                        </Link>
                    })}
                </List>
            </Drawer>
        </div>
    )
}

export default SideNav;
