import React, { useContext } from "react";

import { TeamContext } from "../context/TeamContext";

import { Grid, Paper, Chip, Tooltip, Avatar } from "@material-ui/core/";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  container: {
    display: "flex",
    justifyContent: "center"
  },
  paper: {
    height: "80vh",
    width: "100%",
    marginTop: 25,
    backgroundImage:
      "url('https://upload.wikimedia.org/wikipedia/commons/d/d0/Cricket_field_blank.svg')",
    backgroundSize: "100% 100%",
    display: "flex",
    justifyContent: "space-evenly",
    alignItems: "center"
  }
});

const FantasyTeam = () => {
  const classes = useStyles();
  const { team } = useContext(TeamContext);
  const handleDelete = (pid) => {
    console.log(pid);
  }
  return (
    <div className={classes.container}>
      <Grid item xs={9}>
        <Paper className={classes.paper}>
          {team.map((player) => {
            return <Tooltip title={player.name} arrow><Chip avatar={<Avatar alt={player.name} src={player.imageURL} label={player.name} onDelete={() => handleDelete(player.pid)} />}></Chip></Tooltip>;
          })}
        </Paper>
      </Grid>
    </div>
  );
};

export default FantasyTeam;
