import React, {useState} from 'react';

import { Link } from "react-router-dom";
import MenuIcon from '@material-ui/icons/Menu';
import {Drawer, List, ListItem, ListItemText} from '@material-ui/core/';

const navBars = [{text: "My Team", link: "/fantasy-team"}, {text: "Search Player", link: "/search-player"}, {text: "Live Score", link: "/fantasy-team"}];

const SideNav = () => {
    const [state, setState] = useState(false);
    return (
        <div className="sidenav">
            <MenuIcon color="primary" onClick={() => setState(!state)} />
            <Drawer anchor="left" open={state} onClose={() => setState(false)}>
                <List>
                    {navBars.map(nav => {
                        return <Link style={{textDecoration: "none"}} to={nav.link}>
                            <ListItem>
                                <ListItemText primary={nav.text} />
                            </ListItem>
                        </Link>
                    })}
                    <ListItem>
                        <ListItemText primary="Create Team" />
                    </ListItem>
                </List>
            </Drawer>
        </div>
    )
}

export default SideNav;
