import React, { useContext } from "react";

import { TeamContext } from "../context/TeamContext";

import { Grid, Paper, Avatar, Tooltip } from "@material-ui/core/";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  container: {
    display: "flex",
    justifyContent: "center"
  },
  paper: {
    height: "80vh",
    width: "100%",
    marginTop: 25,
    backgroundImage:
      "url('https://upload.wikimedia.org/wikipedia/commons/d/d0/Cricket_field_blank.svg')",
    backgroundSize: "100% 100%",
    display: "flex",
    justifyContent: "space-evenly",
    alignItems: "center"
  }
});

const toolTipText = `
Name: ${player.name}
Age: ${player.currentAge}
Role: ${player.playingRole}
`

const FantasyTeam = () => {
  const classes = useStyles();
  const { team } = useContext(TeamContext);
  return (
    <div className={classes.container}>
      <Grid item xs={9}>
        <Paper className={classes.paper}>
          {team.map((player) => {
            return <Tooltip title={`
              Name: ${player.name}
              Age: ${player.currentAge}
              Role: ${player.playingRole}
            `} arrow><Avatar alt={player.name} src={player.imageURL}></Avatar></Tooltip>;
          })}
        </Paper>
      </Grid>
    </div>
  );
};

export default FantasyTeam;
